mapper.select:
SELECT 

FROM sys_express a

WHERE a.id = #{id}

SELECT 

FROM sys_express a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.express_id = #{expressId}


		AND a.phone = #{phone}


		AND a.state = #{state}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_express a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_express SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
