mapper.select:
SELECT 

FROM sys_wx_info a

WHERE a.id = #{id}

SELECT 

FROM sys_wx_info a

WHERE a.id_card = #{idCard}

SELECT 

FROM sys_wx_info a

WHERE a.open_id = #{openId}

SELECT 

FROM sys_wx_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.open_id = #{openId}


		AND a.id_card = #{idCard}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_wx_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_wx_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
