SELECT
FROM sys_wx_user_check a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.state = '0'
AND a.id_card = #{idCard}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.phone = #{phone}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

