mapper.select:
SELECT 

FROM sys_wx_user a

WHERE a.id = #{id} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT 

FROM sys_wx_user a

WHERE a.id_card = #{idCard} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT 

FROM sys_wx_user a

WHERE a.phone = #{phone} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT 

FROM sys_wx_user a

WHERE a.id_card = #{0} AND a.phone = #{1}

SELECT 

FROM sys_wx_user a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id_card = #{idCard}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.phone = #{phone}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_wx_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_wx_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
